'use strict';

let escape = require('escape-html');

class BaseElement {

	constructor(opts = {}) {
		this.opts = opts;
	}

	renderAttrs() {
		return Object.keys(this.opts || {}).reduce((cur, key)=>{
			return cur += escape(key)+'="'+escape(this.opts[key])+ '"';
		}, '');
	}

	[Symbol.toPrimitive]() {
		return this.toString();
	}

}

module.exports = BaseElement;