'use strict';

let BaseElement = require('./BaseElement');

class FormOpen extends BaseElement {

  constructor(opts) {
    super(opts);
    this.post();
  }

  method(val) {
    this.opts.method = val;
    return this;
  }

  get() {
    this.method('GET');
    return this;
  }

  put() {
    this.method('PUT');
    return this;
  }

  post() {
    this.method('POST');
    return this;
  }

  encodingType(enctype) {
    this.opts.enctype = enctype;
    return this;
  }

  multipart() {
    this.encodingType('multipart/form-data');
    return this;
  }

  toString() {
    return ['<form',
              this.renderAttrs(),
            '>'].join(' ');
  }

}

module.exports = FormOpen;