'use strict';

let escape = require('escape-html');

let Input = require('./Input');

class InputRadio extends Input {

  constructor(opts, data) {
    super(opts, data);
    this.setAttr('type', 'radio');
  }

  _restore() {
    let name;
    if (!(name = this.opts.name)) return;
    if (!(name in this.data)) return;
    this.deleteAttr('checked');
    if (this.data[name] != this.getAttr('value')) return;
    this.setAttr('checked',true);
  }

}

module.exports = InputRadio;