'use strict';

let escape = require('escape-html');

let Input = require('./Input');

class InputTextarea extends Input {

	render () {

		this._restore();
		
		return [
						'<textarea',
							this.renderAttrs(),
						'>',
						escape(this.opts.value || ''),
						'</textarea>'
					].join(' ');
	}

}

module.exports = InputTextarea;